export const AMAZON_SITESTRIPE_BY_SET_ID: Record<string, string> = {
  // "43251": "https://www.amazon.com/your-sitestripe-link",
};

const AMAZON_RETAILER = "AMAZON";
const AMAZON_SITESTRIPE_AUTO = buildAmazonSitestripeMap(AMAZON_SITESTRIPE_URLS);

function buildAmazonSitestripeMap(urls: string[]) {
  const out: Record<string, string> = {};

  for (const raw of urls ?? []) {
    const url = String(raw ?? "").trim();
    if (!url) continue;

    let slug = "";
    try {
      const parsed = new URL(url);
      const path = String(parsed.pathname ?? "");
      slug = path.split("/dp/")[0] ?? path;
    } catch {
      continue;
    }

    const matches = slug.match(/\b\d{4,6}\b/g) ?? [];
    if (!matches.length) continue;

    const preferred =
      matches.find((m) => m.length === 5) ??
      matches.find((m) => m.length === 4) ??
      matches.find((m) => m.length === 6) ??
      matches[0];

    if (!preferred) continue;
    if (!out[preferred]) out[preferred] = url;
  }

  return out;
}

export function getAmazonSitestripeUrl(setId: string | null | undefined): string | null {
  const key = String(setId ?? "").trim();
  if (!key) return null;
  const url = AMAZON_SITESTRIPE_BY_SET_ID[key] ?? AMAZON_SITESTRIPE_AUTO[key];
  if (typeof url !== "string") return null;
  const trimmed = url.trim();
  return trimmed ? trimmed : null;
}

export function applyAmazonSitestripeToOffers<T extends { retailer?: string | null; url?: string | null }>(
  offers: T[],
  setId: string
): T[] {
  const sitestripeUrl = getAmazonSitestripeUrl(setId);
  const out: T[] = [];
  let sawAmazon = false;

  for (const o of offers ?? []) {
    const key = String(o?.retailer ?? "").trim().toUpperCase();
    if (key !== AMAZON_RETAILER) {
      out.push(o);
      continue;
    }

    sawAmazon = true;
    if (sitestripeUrl) {
      out.push({ ...o, retailer: "Amazon", url: sitestripeUrl });
    } else {
      out.push(o);
    }
  }

  if (sitestripeUrl && !sawAmazon) {
    out.push({ retailer: "Amazon", url: sitestripeUrl } as T);
  }

  return out;
}
